AddCSLuaFile("shared.lua")AddCSLuaFile("cl_init.lua")include("cl_init.lua")include("shared.lua")function ENT:Initialize()	self.ItemData = {}		self:SetModel("models/props_interiors/BathTub01a.mdl")		self:PhysicsInit(SOLID_VPHYSICS)	self:SetMoveType(MOVETYPE_VPHYSICS)	self:SetSolid(SOLID_VPHYSICS)		local phys = self:GetPhysicsObject()		if phys:IsValid() then		phys:Wake()	end		self.ItemData.Think = function()		self:SetColor(math.random(1, 255), math.random(1, 255), math.random(1, 255))	endendfunction ENT:SetItemData(ItemData)	if ItemData.model then		self:SetModel(ItemData.model)	end		if ItemData.skin then		self:SetSkin(ItemData.skin)	end		if ItemData.color then		self:SetColor(ItemData.color)	endendfunction ENT:Use(activator, caller)	self.ItemData.Use()end function ENT:Think()	self.ItemData.Think()end