require("datastream")function showItemWindow(handler, id, encoded, decoded)				local Title = decoded[1]		local ItemList = decoded[2]		local Gui = decoded[3]				if(Gui == 1)then		Gui1(Title)		end				if(Gui == 2)then		Gui2(Title)		end				if(Gui == 3)then		Gui3(Title)		end				endfunction Gui1(Title)	window_npc = vgui.Create("DFrame")	window_npc:SetTitle(Title)	window_npc:SetSize(700,230)	window_npc:ShowCloseButton(true)	window_npc:SetPos(0,30)	window_npc:MakePopup()	window_npc:SetDeleteOnClose(true)					function window_npc:Paint()		surface.SetDrawColor( 0, 0, 50, 100 )		surface.DrawRect( 0, 0, window_npc:GetWide(), window_npc:GetTall() )		surface.DrawRect( 0, 0, window_npc:GetWide(),22)	end									local Itemlist = vgui.Create( "DPanelList", window_npc )	Itemlist:SetPos( 5,25 )	Itemlist:SetSize( 401, 202 )	Itemlist:SetSpacing(0)	Itemlist:EnableHorizontal( true )	Itemlist:EnableVerticalScrollbar( true )		local html = vgui.Create( "HTML", window_npc)	html:SetHTML("")	html:SetSize(700-416, 202 )	html:SetPos(411,25)		endfunction Gui2(Title)		window_npc = vgui.Create("DFrame")	window_npc:SetTitle(Title)	window_npc:SetSize(700,230)	window_npc:ShowCloseButton(true)	window_npc:SetPos(0,30)	window_npc:MakePopup()	window_npc:SetDeleteOnClose(true)			local Itemlist = vgui.Create( "DPanelList", window_npc )	Itemlist:SetPos( 5,25 )	Itemlist:SetSize( 690, 202 )	Itemlist:SetSpacing(0)	Itemlist:EnableHorizontal( true )	Itemlist:EnableVerticalScrollbar( true )	endfunction Gui3(Title)		window_npc = vgui.Create("DFrame")	window_npc:SetTitle(Title)	window_npc:SetSize(400,330)	window_npc:ShowCloseButton(true)	window_npc:SetPos(0,30)	window_npc:MakePopup()	window_npc:SetDeleteOnClose(true)	local html = vgui.Create( "HTML", window_npc)	html:SetHTML("")	html:SetSize(390, 302 )	html:SetPos(5,25)enddatastream.Hook( "showWindow",showItemWindow)