include("shared.lua")require("datastream")AddCSLuaFile( "shared.lua" )AddCSLuaFile( "cl_init.lua" )AddCSLuaFile( "cl_gui.lua" )function ENT:Initialize()		self:SetUseType(3)	self:SetHullType( HULL_HUMAN );	self:SetHullSizeNormal();	self:SetSolid( SOLID_BBOX ) 	self:SetMoveType( MOVETYPE_STEP ) 	self:CapabilitiesAdd(CAP_ANIMATEDFACE | CAP_TURN_HEAD) 	self:SetHealth(100)	  endfunction ENT:SetSpec(id , name, model, itemlistid,opensound,gui)		self.m_id 		= id	self.m_model 	= model	self.m_itemlist = itemlistid	self.m_name 	= name	self.m_osound   = opensound	self.m_gui 		= gui		self:SetModel(self.m_model)		endfunction ENT:AcceptInput(name, activator, caller, data) 	local count = table.Count(self.m_osound)	local snd =  math.random(1,count)		self:EmitSound(self.m_osound[snd])		datastream.StreamToClients(activator,"showWindow",{self.m_name,self.m_itemlist,self.m_gui})	end